import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styles from './PickUpRequestForm.module.css';

export default function PickUpRequestForm() {
    const [availableFood, setAvailableFood] = useState([]);
    const [selectedFoodId, setSelectedFoodId] = useState('');
    const [charityId, setCharityId] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    // Fetch available food donations
    useEffect(() => {
        axios.get('/available')
            .then((response) => {
                setAvailableFood(response.data);
                setLoading(false);
            })
            .catch((error) => {
                setError('Error fetching available food');
                setLoading(false);
            });
    }, []);

    // Handle the form submission for requesting a food pickup
    const handleSubmit = (e) => {
        e.preventDefault();
        if (!selectedFoodId || !charityId) {
            setError('Please select food and enter charity ID.');
            return;
        }

        const requestData = {
            food_id: selectedFoodId,
            charity_id: charityId,
        };

        axios.post('/request', requestData)
            .then((response) => {
                setSuccessMessage('Pickup request submitted successfully!');
                setError('');
                setSelectedFoodId(''); // Clear the selection
                setCharityId(''); // Clear the charity ID
            })
            .catch((error) => {
                setError('Error submitting pickup request.');
                setSuccessMessage('');
            });
    };

    if (loading) {
        return <div className={styles.container}>Loading available food...</div>;
    }

    return (
        <div className={styles.container}>
            <h1>Request a Food Pickup</h1>
            {successMessage && <p className={styles.successMessage}>{successMessage}</p>}
            {error && <p className={styles.errorMessage}>{error}</p>}
            <form onSubmit={handleSubmit} className={styles.form}>
                <label className={styles.label}>
                    Select Food Item:
                    <select
                        value={selectedFoodId}
                        onChange={(e) => setSelectedFoodId(e.target.value)}
                        className={styles.select}
                    >
                        <option value="">--Select Food--</option>
                        {availableFood.map((food) => (
                            <option key={food.food_id} value={food.food_id}>
                                {food.food_name} (Quantity: {food.quantity})
                            </option>
                        ))}
                    </select>
                </label>

                <label className={styles.label}>
                    Charity ID:
                    <input
                        type="text"
                        value={charityId}
                        onChange={(e) => setCharityId(e.target.value)}
                        className={styles.input}
                    />
                </label>

                <button type="submit" className={styles.submitButton}>
                    Submit Request
                </button>
            </form>
        </div>
    );
}
