import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styles from './PickUpRequestForm.module.css';

export default function PickUpRequestForm({ selectedFoodId, setSelectedFoodId }) {
    const [availableFood, setAvailableFood] = useState([]);
    const [charityId, setCharityId] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    // Fetch available food donations
    useEffect(() => {
        axios.get('/available')
            .then((response) => {
                setAvailableFood(response.data);
                setLoading(false);
            })
            .catch((error) => {
                setError('Error fetching available food');
                setLoading(false);
            });
    }, []);

    // Handle the form submission for requesting a food pickup
    const handleSubmit = (e) => {
        e.preventDefault();
        if (!charityId) {
            setError('Please enter charity ID.');
            return;
        }

        const requestData = {
            food_id: selectedFoodId,
            charity_id: charityId,
        };

        axios.post('/request', requestData)
            .then((response) => {
                setSuccessMessage('Pickup request submitted successfully!');
                setError('');
                setCharityId(''); // Clear the charity ID
                setSelectedFoodId(null); // Clear the selection
            })
            .catch((error) => {
                setError('Error submitting pickup request.');
                setSuccessMessage('');
            });
    };

    if (loading) {
        return <div className={styles.container}>Loading available food...</div>;
    }

    return (
        <div className={styles.container}>
            <h1>Request a Food Pickup</h1>
            {successMessage && <p className={styles.successMessage}>{successMessage}</p>}
            {error && <p className={styles.errorMessage}>{error}</p>}
            <form onSubmit={handleSubmit} className={styles.form}>
                <label className={styles.label}>
                    Selected Food Item:
                    <strong>{availableFood.find(food => food.food_id === selectedFoodId)?.food_name}</strong>
                </label>

                <label className={styles.label}>
                    Charity ID:
                    <input
                        type="text"
                        value={charityId}
                        onChange={(e) => setCharityId(e.target.value)}
                        className={styles.input}
                    />
                </label>

                <button type="submit" className={styles.submitButton}>
                    Submit Request
                </button>
            </form>
        </div>
    );
}
