import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function FoodList() {
    const [foodList, setFoodList] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // Fetch available food donations from the backend
    useEffect(() => {
        axios.get('/available')
            .then((response) => {
                setFoodList(response.data);
                setLoading(false);
            })
            .catch((error) => {
                setError('Error fetching food data');
                setLoading(false);
            });
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h1>Available Food Donations</h1>
            {foodList.length === 0 ? (
                <p>No available food donations.</p>
            ) : (
                <ul>
                    {foodList.map(food => (
                        <li key={food.food_id}>
                            <strong>{food.food_name}</strong><br />
                            Quantity: {food.quantity}<br />
                            Description: {food.description}<br />
                            Expiration Date: {new Date(food.expiration_date).toLocaleDateString()}<br />
                            Status: {food.status}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}
